    val listData: ArrayList<Restaurant>
        get() {
            val list = arrayListOf<Restaurant>()
            for (position in restaurantNames.indices) {
                val restaurant = Restaurant()
                restaurant.name = restaurantNames[position]
                restaurant.city = restaurantCities[position]
                restaurant.photo = restaurantImages[position]
                restaurant.description = restaurantDescriptions[position]
                list.add(restaurant)
            }
            return list
        }
